fetch("https://api.nytimes.com/svc/topstories/v2/world.json?api-key=55NwTRr1fPA9ZChVHPonPlef6sRlaWGG").then(e=>e.json()).then(e=>{document.querySelector("#genre1").innerHTML=`\n    <div class=genreplacement>\n      <div class=genreborder>\n        <img src=assets/image/icon5genre.svg>\n      </div>\n      <h2>${e.results[0].section}</h2>\n      <img src=assets/image/icon7arrowdown.svg class=genreplacement-right>\n    </div>\n  `,e.results.forEach(function(s,n){"world"==s.section&&(document.querySelector("main").innerHTML+=`\n        <div class="container">\n          <div class=deleteitem><img src='assets/image/icon1archive.svg'></div>\n          <div class="jokeitem animate">\n            <img src="assets/image/profile2.svg" class="placearticleitem1">\n            <h3 class="placearticleitem2">${e.results[n].title}</h3>\n            <span class="cardtext placearticleitem2">${e.results[n].abstract}</span>\n          </div>\n        </div>\n      `)})});