fetch("https://jsonplaceholder.typicode.com/users").then(e=>e.json()).then(e=>{e.forEach(e=>{const t=document.createElement("div");t.classList.add("container","placearticle"),t.setAttribute("id",e.id);const i=document.createElement("div");i.classList.add("deleteitem"),i.textContent="Delete",i.innerHTML="<img src='assets/image/icon1archive.svg'>";const s=document.createElement("div");s.classList.add("jokeitem","animate"),s.innerHTML="<img src='assets/image/profile2.svg' class='placearticleitem1'>"+e.name,t.appendChild(i),t.appendChild(s),document.querySelector("main").appendChild(t)})});