const getCSSCustomProp=e=>{let t=getComputedStyle(document.documentElement).getPropertyValue(e);return t.length&&(t=t.replace(/\'|"/g,"").trim()),t},STORAGE_KEY="user-color-scheme",applySetting=e=>{let t=e||localStorage.getItem(STORAGE_KEY);t&&document.documentElement.setAttribute("data-user-color-scheme",t)},toggleSetting=()=>{let e=localStorage.getItem(STORAGE_KEY);switch(e){case null:e="dark"===getCSSCustomProp("--color-mode")?"light":"dark";break;case"light":e="dark";break;case"dark":e="light"}return localStorage.setItem(STORAGE_KEY,e),e};document.querySelector(".js-mode-toggle").addEventListener("click",e=>{e.preventDefault(),applySetting(toggleSetting())}),applySetting();